INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O302', 'Inflammation: de l''asthme aux maladies auto-immunes et auto-inflammatoires', 'Faculté de médecine', 'Apprendre les bases cellulaires et moléculaires de l''inflammation en les intégrant dans le contexte de plusieurs exemples de pathologie humaine auto-immune et auto- inflammatoire.', 'Explorer et approfondir différents aspects de l''inflammation.', 0, 0, 3);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O303', 'Initiation à la recherche en neurosciences psychiatriques (cliniques et fondamentales)', 'Faculté de médecine', null, null, 0, 0, 3);
