INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O307', 'Dans les coulisses des nouvelles technologies médicales', 'Faculté de médecine', 'Ce cours familiarise les étudiants avec quelques technologies prometteuses, leurs bases physiques, les applications visées et leur état actuel de développement.', 'Les technologies médicales représentent aujourd''hui un vaste champ de recherche caractérisé par beaucoup d''innovations et un développement rapide. Les progrès dans cette branche - comme les procédures chirurgicales robotisées, les prothèses neurales ou la télémédecine - sont en constante évolution. Dans ce cours nous aborderons :

   1. Les prothèses neurales et les Interfaces Cerveau-Ordinateur
   2. L''imagerie en coupes: Tomographie.
   3. La robotique Médicale
   4. La télémédicine et l''intelligence artificielle
', 0, 0, 3);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O312', 'Diagnostique physiopathologique de troubles hydroélectrolytiques', 'Faculté de médecine', null, null, 0, 0, 3);
