INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O316', 'Chrirurgie plastique: du laboratoire à la clinique', 'Faculté de médecine', null, null, 0, 0, 3);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O317', 'Elements of Proteomics and Metabolomics: translation to human diseases', 'Faculté de médecine', 'Transmettre les notions de base en protéomique appliquée', 'Introduction à la protéomique biomédicale', 0, 0, 3);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O321', 'Génétique Médicale II (niveau avancé)', 'Faculté de médecine', 'Mettre à jours ses connaissances selon les derniers développements de la génétique humaine, au service de la génétique médicale.', 'Ce cours, qui suit et complète le cours de génétique humaine proposé lors de la session d''automne, propose une revue des concepts les plus avancés dans le décryptage de l''exome, du génome, et des mécanismes liés à sa régulation ou au maintien de son intégrité.', 0, 0, 3);
