INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O404', 'De la douleur aiguë à la douleur chronique: méthodes de traitement minimalement invasif de lutte contre la chronicisation de la douleur (A)', 'Faculté de médecine', null, '

Ceci implique :
- le parcours du diagnostic clinique
- l''imagerie diagnostique
- la discussion multidisciplinaire
- l''information du patient et de sa famille,
- le geste thérapeutique jusqu''au suivi après traitement
', 0, 0, 3);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O405', 'De la douleur aiguë à la douleur chronique: méthodes de traitement minimalement invasif de lutte contre la chronicisation de la douleur (P)', 'Faculté de médecine', null, 'Permettre aux jeunes étudiants de suivre l''aspect multidisciplinaire du diagnostic et des traitements minimalement invasifs concernant les douleurs aiguës et chroniques. Le but du cours est d''exposer l''étudiant à l''approche clinique et éthique de la prise en charge d''un patient souffrant de douleurs persistantes. Une sélection d''interventions chirurgicales minimalement invasives sont effectuées en présence des étudiants au bloc opératoire et commentées.

Ceci implique :
- le parcours du diagnostic clinique
- l''imagerie diagnostique
- la discussion multidisciplinaire
- l''information du patient et de sa famille
- le geste thérapeutique jusqu''au suivi après traitement

', 0, 0, 3);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O408', 'Anesthésiologie et Médecine péri-opératoire: au carrefour de la physiologie et de la pharmacologie appliquées (P)', 'Faculté de médecine', null, null, 0, 0, 3);
