INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O412', 'Human Factors Engineering in Medicine', 'Faculté de médecine', 'Understanding of the Human''Technology interaction in complex environments such as healthcare

Basic knowledge of Human Factors Engineering principles applicable to everyday problems in healthcare

Development of a feeling for good and flawed design of work environments and social systems

Understanding the importance of system design to translate scientific knowledge into clinical practice', 'This course provides the participants with a hands-on introduction to the expanding and fascinating field of human factors engineering. Human Factors is a cross-field discipline drawing from psychology, social science, neuroscience, engineering and design. Human Factors are crucial for patient safety and quality of care.', 0, 0, 3);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('223O415', 'Comprendre l''IRM: du principe physique à la détection de pathologie', 'Faculté de médecine', null, null, 0, 0, 3);
