INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('32C2038', 'Nouvelles technologies de l''information et de la communication', 'Faculté des lettres', null, null, 0, 0, null);
