INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('32C2064', 'Développement web', 'Faculté des lettres', null, null, 0, 0, null);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('32C2065', 'Programmation Web', 'Faculté des lettres', null, 'Ce cours offre une découverte du monde informatique du point de vue de l''utilisateur et du développeur. Il dresse un panorama des nouvelles technologies de l''information et de la communication, des notions de réseau, internet, Web, recherche d''information, e-mail, et aborde de manière introductive les langages de description (HTML, CSS) et les langages de programmation (JavaScript). La programmation web sert de support pour une initiation aux principes fondamentaux de la programmation applicables à tous les langages de programmation comme les variables et les instructions conditionnelles et itératives. Le savoir-faire s''acquiert par le développement de mini-applications durant les séminaires et les travaux pratiques divers.', 0, 0, null);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('32C2066', 'Algorithmique et programmation', 'Faculté des lettres', null, 'Ce cours fait suite au cours de programmation web. Il approfondit les
notions de programmation et d''algorithmique, tant au niveau théorique
qu''au niveau pratique. La syntaxe d''un langage de programmation
particulier, plus modulaire et plus formel, est enseignée comme base à
l''application de ces principes. Les notions enseignées couvrent entre
autres les structures de données statiques et dynamiques, la
récursivité, les algorithmes de tri. Le savoir-faire s''acquiert par le
développement de mini-applications durant les séminaires et les
travaux pratiques.
web: <A HREF="http://dokeos.unige.ch/home/courses/INFO1">http://dokeos.unige.ch/home/courses/INFO1</A>', 0, 0, null);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('32C2067', 'Travaux pratiques : Algorithmique et programmation', 'Faculté des lettres', null, 'Ce cours fait suite au cours de programmation web. Il approfondit les notions de programmation et d''algorithmique, tant au niveau théorique qu''au niveau pratique. La syntaxe d''un langage de programmation particulier, plus modulaire et plus formel, est enseignée comme base à l''application de ces principes. Les notions enseignées couvrent entre autres les structures de données statiques et dynamiques, la récursivité, les algorithmes de tri. Le savoir-faire s''acquiert par le développement de mini-applications durant les séminaires et les travaux pratiques divers.', 0, 0, null);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('32C2117', 'Bases de données - Modélisation des systèmes d''information et des services', 'Faculté des lettres', null, null, 0, 0, null);
