INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('32G2503', 'Expression orale II', 'Faculté des lettres', null, null, 0, 0, null);
