INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('32J0464', 'Conditions de vie et luttes féminines en Suisse (19e-20e siècles)', 'Faculté des lettres', null, 'En Suisse, le droit de vote et d''éligibilité n''est obtenu par les femmes qu''en 1971 et le principe d''égalité entre hommes et femmes est inscrit dans la Constitution fédérale en 1981. Pourtant, les premières véritables mobilisations féminines remontent à la deuxième moitié du 19e siècle. Comment expliquer ce décalage, alors que la Confédération helvétique s''affirme comme l''une des « plus anciennes démocraties du monde » ?
Dans ce séminaire, nous étudierons quelques moments forts de la lutte pour l''égalité en Suisse, de l''adoption de la Constitution fédérale en 1848 à la grève des femmes en 1991. Sur la base d''analyse de sources, nous questionnerons l''influence des mobilisations féminines et féministes sur le système démocratique suisse et l''ordre traditionnel de genre, ainsi que les freins imposés par ceux-ci à l''égalité des droits et à la remise en cause de la répartition genrée des rôles.
', 0, 0, null);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('32J0467', 'Abus, déviances et sorcellerie dans le monde alpin (XVe-début XVIe s.) : entre réforme et répression', 'Faculté des lettres', null, null, 0, 0, null);
INSERT INTO pt1.cours (id_cours, nom_cours, faculte, objectif, descriptif, obligatoire, option_obligatoire, nb_credits) VALUES ('32J0468', 'Entre les monts, le monde des principautés alpines (XIV-début XVIe s.)', 'Faculté des lettres', null, null, 0, 0, null);
